t.script:
     document.addEventListener("DOMContentLoaded", function () {
    let htmlLang = document.documentElement.lang || 'en';

    if (htmlLang.startsWith('de')) {
        document.querySelector('.lang-de').style.display = 'block';
        document.querySelector('.lang-en').style.display = 'none';
    } else {
        document.querySelector('.lang-en').style.display = 'block';
        document.querySelector('.lang-de').style.display = 'none';
    }
});

                // Input validation
                document.addEventListener('DOMContentLoaded', function() {
                    const inputs = document.querySelectorAll('.gdfh-input');

                    inputs.forEach(input => {
                        input.addEventListener('input', function() {
                            if (input.checkValidity() && input.value.trim() !== '') {
                                input.classList.add('valid');
                                input.classList.remove('invalid');
                            } else if (!input.checkValidity() && input.value.trim() !== '') {
                                input.classList.add('invalid');
                                input.classList.remove('valid');
                            } else {
                                input.classList.remove('valid', 'invalid');
                            }
                        });
                    });
                });

                // Fetch previous result
                function fetchPreviousResult() {
                    var email = document.getElementById("input_email").value;
                    if (email) {
                        fetch("/GDfH_Scoring_Test/fetch_result?email=" + encodeURIComponent(email))
                            .then(res => res.json())
                            .then(data => {
                                let output = "";
                                if (data.found) {
                                    output += "<div class='gdfh-result-item'><strong>Name:</strong><br/>" + data.name + "</div>";
                                    output += "<div class='gdfh-result-item'><strong>Email:</strong><br/>" + data.email + "</div>";
                                    output += "<div class='gdfh-result-item'><strong>Country:</strong><br/>" + data.country + "</div>";
                                    output += "<div class='gdfh-result-item'><strong>Total Score:</strong><br/><span style='font-size:1.5rem; color:#00B800; font-weight:700;'>" + data.total_score + "/180</span></div>";
                                    output += "<div class='gdfh-result-item' style='margin-top:15px; padding:10px; background:#f8f9fa; border-radius:8px;'><strong>Level:</strong><br/><span style='color:#00B800;'>" + data.level + "</span></div>";
                                } else {
                                    output = "<div style='text-align:center; padding:20px; color:#7f8c8d;'>No previous result found.</div>";
                                }
                                document.getElementById("result_output").innerHTML = output;
                            })
                            .catch(error => {
                                console.error('Error fetching result:', error);
                            });
                    }
                }
