record.field:
records = env['account.move'].search([
    ('move_type', '=', 'out_invoice'),
    ('invoice_date_due', '!=', False),
    ('payment_state', 'in', ('not_paid', 'partial')),
])

template = env.ref('land_plot_manager.email_invoice_due_reminder', raise_if_not_found=False)

if template:
    for rec in records:
        if rec._check_due_date_for_reminder():
            if rec.partner_id and rec.partner_id.email:
                template.send_mail(rec.id, force_send=True)
