template.script:
(function() {
'use strict';

function initTabs() {
var tabs = document.querySelectorAll('.project-tab');
if (tabs.length === 0) {
// Retry if DOM not ready
setTimeout(initTabs, 100);
return;
}

tabs.forEach(function(btn) {
btn.onclick = function(e) {
e.preventDefault();
document.querySelectorAll('.project-tab').forEach(function(b) {
b.classList.remove('active');
});
document.querySelectorAll('.projects-section').forEach(function(sec) {
sec.classList.remove('projects-section--active');
});
var target = document.querySelector(this.getAttribute('data-target'));
if (target) {
this.classList.add('active');
target.classList.add('projects-section--active');
}
};
});
}

// Initialize when DOM is ready
if (document.readyState === 'loading') {
document.addEventListener('DOMContentLoaded', initTabs);
} else {
initTabs();
}
})();
document.addEventListener("DOMContentLoaded", function() {
let htmlLang = document.documentElement.lang || 'en';
if (htmlLang.startsWith('de')) {
document.querySelector('.lang-de').style.display = 'block';
} else {
document.querySelector('.lang-en').style.display = 'block';
}
});

t.script:
document.querySelectorAll('.project-tab').forEach(btn => {
btn.addEventListener('click', function () {
document.querySelectorAll('.project-tab').forEach(b => {
b.classList.remove('active');
b.style.backgroundColor = '#ecfdf5';
b.style.color = '#017F36';
});

this.classList.add('active');
this.style.backgroundColor = '#1ba14b';
this.style.color = 'white';

document.querySelectorAll('.tab-content-area').forEach(tab => tab.style.display = 'none');
const target = this.getAttribute('data-target');
if (target) {
document.querySelector(target).style.display = 'block';
}
});
});
